mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail}, manufacture_day = #{manuDate} , image_file = #{fileName}, price=#{price}
WHERE prod_no = #{prodNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT p.PROD_NO,p.PROD_NAME,p.PROD_DETAIL,p.MANUFACTURE_DAY,p.PRICE,p.IMAGE_FILE,p.REG_DATE,t.TRAN_STATUS_CODE 
										FROM transaction t, product p

										t.prod_no(+)=p.prod_no


									 				and p.PROD_NO = #{searchKeyword}


									 				and p.prod_name = #{searchKeyword}


									 				and p.PRICE = #{searchKeyword}



										ORDER BY p.prod_no ) inner_table

					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT prod_no , prod_name ,price
					FROM product



					 			prod_no = #{searchKeyword}


					 		prod_name = #{searchKeyword}


					 		price = #{searchKeyword}


					 ) countTable
