mapper.update:
   	UPDATE transaction

   		RECEIVER_NAME 	= #{receiverName} ,
		RECEIVER_PHONE	= #{receiverPhone},
		DEMAILADDR 				= #{divyAddr} ,
		DLVY_REQUEST				=	#{divyRequest},
		DLVY_DATE=#{divyDate}
		purchase_count=#{purchaseCount}

   	WHERE tran_no = #{tranNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT * FROM transaction
buyer_id=#{buyerId}
ORDER BY tran_no ) inner_table
WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*) FROM ( SELECT * FROM ( SELECT * FROM transaction
buyer_id=#{buyerId}
ORDER BY tran_no ) countTable )
